import { Row, Tag, Checkbox } from 'antd';
import { useState } from 'react';
import { useDispatch } from 'react-redux';
// import { toggleTodoStatus } from '../../redux/actions';
import todoListSlice from '../TodoList/todoListSlice';
import { EditFilled, DeleteFilled } from '@ant-design/icons';
import { deleteTodo } from "../../service/todoService";

const priorityColorMapping = {
    High: 'red',
    Medium: 'blue',
    Low: 'gray',
};

export default function Todo({ todoId, name, priority, completed }) {
    const dispatch = useDispatch();
    const [checked, setChecked] = useState(completed);

    const toggleCheckbox = () => {
        setChecked(!checked);
        dispatch(todoListSlice.actions.toggleTodoStatus(todoId))
    };

    const deleteTodo = async (todoId) => {
        await deleteTodo(todoId);
    }

    return (
        <Row
            justify='space-between'
            style={{
                marginBottom: 3,
                ...(checked ? { opacity: 0.5 } : {}),
            }}
        >
            <Checkbox checked={checked} onChange={toggleCheckbox}>
                {name}
            </Checkbox>
            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
                <Tag color={priorityColorMapping[priority]} style={{ margin: 0 }}>
                    {priority}
                </Tag>
                <div className="action-buttons" style={{ display: 'flex', flexDirection: 'row', marginLeft: '8px' }}>
                    {/* <EditFilled style={{color: '#1890ff', cursor: 'pointer', marginLeft: '8px', fontSize: '16px'}}/> */}
                    <DeleteFilled style={{ color: '#cf1322', cursor: 'pointer', marginLeft: '8px', fontSize: '16px' }} onClick={deleteTodo(todoId)} />
                </div>
            </div>
        </Row>
    );
}
